def determineFloorLevel(input):
    numberOfOpeningParentheses = input.count('(')
    numberOfClosingParentheses = input.count(')')
    return numberOfOpeningParentheses - numberOfClosingParentheses

def firstEntryToBasement(input):
    listOfCharacters = list(input)
    numericValuesOfCharacters = [convertToOneOrMinusOne(x) for x in listOfCharacters]
    runningTotals = [sum(numericValuesOfCharacters[0:y+1]) for y in range(len(numericValuesOfCharacters))]
    return runningTotals.index(-1) + 1

def convertToOneOrMinusOne(parenthesis):
    if parenthesis == '(':
        return 1
    if parenthesis == ')':
        return -1
    else:
        raise('invalid input string, expected ( or ), but got: ' + parenthesis)

